import React, { useState } from "react";
import { Link } from "react-router-dom";

const Navbar = ({ onLogout, userData }) => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  return (
    <nav
      style={{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        padding: "10px 20px",
        background: "#1976d2",
        color: "#fff",
      }}
    >
      {/* Left links */}
      <div>
        <Link to="/" style={{ color: "#fff", marginRight: "15px" }}>
          Summary
        </Link>
        <Link to="/orders" style={{ color: "#fff", marginRight: "15px" }}>
          Orders
        </Link>
        <Link to="/holdings" style={{ color: "#fff", marginRight: "15px" }}>
          Holdings
        </Link>
        <Link to="/positions" style={{ color: "#fff", marginRight: "15px" }}>
          Positions
        </Link>
        <Link to="/funds" style={{ color: "#fff", marginRight: "15px" }}>
          Funds
        </Link>
        <Link to="/apps" style={{ color: "#fff" }}>
          Apps
        </Link>
      </div>

      {/* Right: Username dropdown */}
      <div style={{ position: "relative" }}>
        {userData && (
          <div
            onClick={toggleDropdown}
            style={{
              cursor: "pointer",
              background: "#1565c0",
              padding: "8px 12px",
              borderRadius: "5px",
              userSelect: "none",
            }}
          >
            {userData.username} â¬‡
          </div>
        )}

        {/* Dropdown Menu */}
        {dropdownOpen && (
          <div
            style={{
              position: "absolute",
              top: "40px",
              right: "0",
              background: "#fff",
              color: "#000",
              border: "1px solid #ddd",
              borderRadius: "5px",
              boxShadow: "0px 4px 8px rgba(0,0,0,0.1)",
              minWidth: "120px",
              zIndex: 100,
            }}
          >
            <button
              onClick={onLogout}
              style={{
                width: "100%",
                padding: "10px",
                border: "none",
                background: "transparent",
                cursor: "pointer",
                textAlign: "left",
              }}
            >
              ðŸšª Logout
            </button>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
